import './App.css';
import React, { useState } from 'react';

function App() {

  const [forecast, setForecast] = useState([]);
  const [city, setCity] = useState([]);
  const [countryCode, setCountryCode] = useState([]);
  const [errorMessage, setErrorMessage] = useState([]);

  function fetchRequest (){
    console.log(city)
    console.log(countryCode)
    fetch(`https://localhost:44396/weatherforecast/forecast?city=${city}&countryCode=${countryCode}`, { headers: {
      'ClientId': 'Bob',
      'content-type': 'application/json'
    }})
      .then(res => res.json())
      .then(response => {
        setForecast(response);
      })
      .catch(error => 
        setForecast('Invalid city')
        );
};

  return (
    <div className="App">
      <h1>Weather Forecast</h1>
      <label>
        Country Code (ISO-3661-2 Format):
        <input
          type="text"
          value={countryCode}
          onChange={e => setCountryCode(e.target.value)}
        />
      </label>
      <br/>
      <label>
        City:
        <input
          type="text"
          value={city}
          onChange={e => setCity(e.target.value)}
        />
      </label>
      <br/>
      <label>{errorMessage}</label>
      <button className="button" onClick={fetchRequest}> Submit </button>
      <div>
        <h2>{forecast}</h2>
      </div>
    </div>
  );
}

export default App;
